sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//Type appropriate comment here, and begin script below

	g_form.clearOptions("u_what_needed");
	if(newValue == "hr") {
		g_form.addOption("u_what_needed","hr","Human Resources 1");
		g_form.addOption("u_what_needed","hr","Human Resources 2");
		g_form.addOption("u_what_needed","hr","Human Resources 3");
	}
	if(newValue == "facilities") {
		g_form.addOption("u_what_needed","facilities","Facilities 1");
		g_form.addOption("u_what_needed","facilities","Facilities 2");
		g_form.addOption("u_what_needed","facilities","Facilities 3");
	}
	if(newValue == "legal") {
		g_form.addOption("u_what_needed","legal","Legal 1");
		g_form.addOption("u_what_needed","legal","Legal 2");
		g_form.addOption("u_what_needed","legal","Legal 3");
	}



}
